// script for single key toggle mode
var keyVal = value[getIndex(id)]
console.log('key value: ' + keyVal + ' (on: '+getProp('this', 'on')+')')
if (keyVal == getProp('this', 'on')) {
  // if key is pressed
  console.log('last key: '+locals.lastKey)
  if (locals.lastKey) {
    // release last pressed key
    // sync:false to prevent forced release from triggering the script 
    set(locals.lastKey, 0, {sync:false})
  }
  // store pressed key 
  locals.lastKey = id
} else if (id == locals.lastKey ) {
  // if key is released manually
  locals.lastKey = 0
}
console.log('current key: '+id, 'current index: '+getIndex(id))
