widgets.onValue:
console.log("bass points: " + value)
if (Array.isArray(value)) {
  const split = value.length/3;
  // first 1/3 of the array are points
  // second 1/3 are 2D coordinates
  const pts = value.slice(0, split)
  const pos = value.slice(split, value.length)
  console.log("pts: " + pts)
  console.log("pos: " + pos)

  setVar('bass_line', 'pts', pts)
  set('bass_line', pos)
} else {
  setVar('bass_line', 'pts', [])
  set('bass_line', [])
}

