widgets.css:
.pad-@{bass_beat_point} {
  --color-fill: darkorange;
  //--color-stroke: green;
}

JS{{
var sel_points = @{selected_pts_bass}
if (sel_points.length > 0) {
  var css = ''
  for (var i = 0; i < sel_points.length; i++) {
    css += ".pad-" + sel_points[i] + "{--color-fill: red; /*--color-stroke: red;*/}\n"
  }
  return css
}
}}

widgets.pointSize:
JS{{
    // Return value 24-10 for point counts between (16 and under) - (48 and over) 
    if (@{bass_pt_count} <= 16) {
        return 24;
    } else if (@{bass_pt_count} >= 48) {
        return 10;
    } else {
        return 10 + (24 - 10) * (48 - @{bass_pt_count}) / (48 - 16);
    }
}}

widgets.rangeX:
JS{{    
  var hash = {};
  var chunks = @{bass_pt_count}
  for (var i = 0; i < chunks; i++) {
    hash[(i / (chunks-1)) * 100 + "%"] = i+1;
  }
  return hash;
}}

widgets.onTouch:
// GH - "Snap-to-grid" script adjusting all coordinates to nearest whole number
if ((event.handle !== undefined) && (event.type === 'stop')) {
  for (var i=0; i<value.length; i++) {
    value[i] = Number(value[i].toFixed())
  }
  set(this, value)
  send('/bass_line_updated', ...value)
  // also add/remove touched point to/from the array
  // if in delete mode
  if (get("bass_del") == 1) {
    const pts = get("selected_pts_bass")
    const index = pts.indexOf(event.handle);
    if (index > -1) { // only splice array when item is found
      pts.splice(index, 1) // 2nd parameter means remove one item only
    }
    else {
      pts.push(event.handle)
    }
    set("selected_pts_bass", pts)
  }
}

widgets.onValue:
if (value === 0) {
  if (get("delete_bass") > 0) {
  set("delete_bass", 0)
  send('/bass_delete', ...get("selected_pts_bass"))
  if (get('bass_auto') === 0) { set('bass_update', 1)}
  }
}
else { 
  set("bass_rec", 0)
  set("bass_del_cancel", 1) 
  set("delete_bass", 1)
}
set("selected_pts_bass", [])

